import React, { Component } from 'react';
import './App.css';
import firebase from './firebase.js'; // <--- add this line
import { PanelGroup, Panel, Button, ButtonToolbar, ListGroup, ListGroupItem } from 'react-bootstrap';

class App extends Component {
  constructor() {
    super();
    this.state = {
      ingredients: '',
      title: '',
      items: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit(e) {
    e.preventDefault();
    const itemsRef = firebase.database().ref('items');
    const item = {
      ingredients: this.state.ingredients,
      name: this.state.title
    }
    itemsRef.push(item);
    this.setState({
      ingredients: '',
      title: ''
    });
  }

  componentDidMount() {
    const itemsRef = firebase.database().ref('items');
    itemsRef.on('value', (snapshot) => {
      let items = snapshot.val();
      let newState = [];
      for (let item in items) {
        newState.push({
          id: item,
          name: items[item].name,
          ingredients: items[item].ingredients
        });
      }
      this.setState({
        items: newState
      });
    });
  }

  render() {
    return (
      <div className='app jumbotron'>
        <header>
          <div className='wrapper'>
            <h1>Reciepe box</h1>

          </div>
        </header>
        <div className='container'>
          <section className="add-item">
            <form onSubmit={this.handleSubmit}>
              <input type="text" name="title" placeholder="Name of recipe?" onChange={this.handleChange} value={this.state.title} />
              <input type="text" name="ingredients" placeholder="Ingredients?" onChange={this.handleChange} value={this.state.ingredients} />

            </form>
          </section>
          <section className='display-item'>
            <div className="wrapper">
              <ul>

                {this.state.items.map((item) => {
                  return (
                    <li key={item.id}>
                      <h3>{item.name}</h3>
                      <p>Ingredients: {item.ingredients}</p>
                    </li>
                  )
                })}

              </ul>
              <Button type="submit">Add reciepe</Button>
            </div>
          </section>
        </div>
      </div>
    );
  }
}
export default App;
