import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      ingredients: '',
      title: ''
    }
    this.handleChange = this.handleChange.bind(this);
  }


  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }


  render() {
    return (
      <div className='app'>
        <header>
          <div className='wrapper'>
            <h1>Fun Food Friends</h1>

          </div>
        </header>
        <div className='container'>
          <section className="add-item">
            <form>
              <input type="text" name="title" placeholder="What's your name?" onChange={this.handleChange} value={this.state.title} />
              <input type="text" name="ingredients" placeholder="What are you bringing?" onChange={this.handleChange} value={this.state.ingredients} />
              <button>Add reciepe</button>
            </form>
          </section>
          <section className='display-item'>
            <div className='wrapper'>
              <ul>
              </ul>
            </div>
          </section>
        </div>
      </div>
    );
  }
}
export default App;
