import React, { Component } from 'react';
import './App.css';
import firebase from './firebase.js'; // <--- add this line

class App extends Component {
  constructor() {
    super();
    this.state = {
      ingredients: '',
      title: ''
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit(e) {
    e.preventDefault();
    const itemsRef = firebase.database().ref('items');
    const item = {
      title: this.state.currentItem,
      user: this.state.username
    }
    itemsRef.push(item);
    this.setState({
      currentItem: '',
      username: ''
    });
  }

  render() {
    return (
      <div className='app'>
        <header>
          <div className='wrapper'>
            <h1>Reciepe box</h1>

          </div>
        </header>
        <div className='container'>
          <section className="add-item">
          <form onSubmit={this.handleSubmit}>
              <input type="text" name="title" placeholder="What's your name?" onChange={this.handleChange} value={this.state.title} />
              <input type="text" name="ingredients" placeholder="What are you bringing?" onChange={this.handleChange} value={this.state.ingredients} />
              <button>Add reciepe</button>
            </form>
          </section>
          <section className='display-item'>
            <div className='wrapper'>
              <ul>
              </ul>
            </div>
          </section>
        </div>
      </div>
    );
  }
}
export default App;
